const burger=document.querySelector(".burger-menu__button"),menu=document.querySelector(".burger-menu");burger.addEventListener("click",(function(e){e.preventDefault(),menu.classList.toggle("active")}));const anchors=document.querySelectorAll('a[href*="#"]');for(let e of anchors)e.addEventListener("click",(function(o){o.preventDefault();const t=e.getAttribute("href");document.querySelector(""+t).scrollIntoView({behavior:"smooth",block:"start"})}));function scrollFunction(){document.body.scrollTop>400||document.documentElement.scrollTop>400?document.querySelector(".arrowUp").style.display="block":document.querySelector(".arrowUp").style.display="none"}document.querySelector(".arrowUp").onclick=function(){document.body.scrollTop=0,document.documentElement.scrollTop=0},window.onscroll=function(){scrollFunction()};